
public class For_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x;
		String[] Car={"BMW","Audi","Toyoto","Mahindra"};
		x=10;
		
		for(int i=0; i < x;i++) {
			System.out.println(i);
		}
		
		for(String Names:Car) {
			System.out.println(Names);
		}

	}

}
